mapper.select:
select r.id, role_name, count(a.id) adminNum
from admin_role r, `admin` a
where r.id = a.role_id and r.id = #{roleId}

select id, role_name, is_lock, version, when_created, when_updated
from admin_role
where id = #{roleId}

select id, role_name, is_lock, version, when_created, when_updated
from admin_role
where id = #{roleName}
